import { useState } from 'react';

const useModal = () => {
    const [isShowing, setIsShowing] = useState(false);

    function toggle() {
      setIsShowing(!isShowing);
    }

    function clearStorage() {
      localStorage.clear();
  }

    return { isShowing, toggle, clearStorage }
}

export default useModal;
