import React from 'react';
import { Link } from 'react-router-dom';
import './../styles/Conversions.css';

export default function ConversionsHistory(props) {

    const { currHistory } = props.location.state;

    function clearStorage() {
        localStorage.clear();
    }

    console.log('ConversionsTest Component', currHistory);

    return (
        <div className="conversion-container">
            <div className="conversion-element">
                <Link to='/'><button className="btn-conversions">Return to the converter</button></Link>
                {currHistory  !== null && (
                    <Link to='/'><button className="btn-conversions" onClick={clearStorage}>Clear history</button></Link>
                )}
            </div>

            <table className="conversion-table">
                {currHistory !== null && (
                    <thead>
                        <tr>
                            <th>Base amount</th>
                            <th>Conversion result</th>
                            <th>Date</th>
                        </tr>
                    </thead>
                )}

                {currHistory  !== null ? currHistory.map(item =>
                    <tbody>
                        <tr>
                            <td><span>{item.amount}&nbsp;</span><span>{item.currFrom}</span></td>
                            <td><span>{item.result}&nbsp;</span><span>{item.currTo}</span></td>
                            <td>{item.date}</td>
                        </tr>
                    </tbody>
                ) : <span className="conversion-info">Historia Konwersji jest pusta</span>}
            </table>
        </div>
    );
}
