import React, { useState, useEffect, useCallback } from 'react';
import { Link, useHistory } from 'react-router-dom';
import CurrencySelect from './CurrencySelect';
import ErrorWall from './Errors';
import Modal from './Modal';
import useModal from './../hooks/useModal'; 

import { CURRENCY_OPTIONS } from './../components/CurrencyOptions';
import './../styles/Converter.css';
import './../styles/Modal.css';


export default function CurrencyConverter() {
const {isShowing, toggle} = useModal();
    const [elements, setElements] = useState(CURRENCY_OPTIONS);
    const [currHistory, setCurrHistory] = useState([]);
    const [amount, setAmount] = useState(1);
    const [result, setResult] = useState("");
    const [error, setError] = useState(null);
    const [date] = useState(new Date().toLocaleString().split(',')[0]);
    const [currFrom, setCurrFrom] = useState("USD");
    const [currTo, setCurrTo] = useState("PLN");
    const currencyAPI = 'https://api.exchangeratesapi.io/latest';
    let history = useHistory();


    //get currency value
    // const getCurrValue = useCallback(() => {
    //     // currencyHistory();
    //     fetch(currencyAPI)
    //         .then(res => res.json())
    //         .then(data => {
    //             const allCurrency = ([data.base, ...Object.keys(data.rates)])
    //             setElements(allCurrency);

    //         })
    //         .catch(err => setError(true))
    // }, [])

    // useEffect(() => {
    //     getCurrValue();
    // }, [getCurrValue]);


    //set currency value
    const convertCurrency = async() => {
        console.log(elements);
        // e.preventDefault();
        await fetch(currencyAPI + '?base=' + currFrom + '&symbols=' + currTo)
            .then(res => res.json())
            .then(data => {
                const amountScore = (amount * (data.rates[currTo])).toFixed(2);
                // const storageHistory = JSON.parse(localStorage.getItem('history')) || [];



                console.log('amountScore', amountScore);

                setResult(amountScore);


                // setCurrHistory(storageHistory);





                // currHistory.push({
                //     amount,
                //     currFrom,
                //     amountResult,
                //     currTo,
                //     date
                // });  



                // setCurrHistory(localStorage.setItem('history', JSON.stringify(currHistory)))

                // currencyHistory();

                // history.push({
                //     // pathname: '/conversions',
                //     state: {
                //         currHistory,
                //     }
                // })

            })

    }

    const handleSelectCurrency = e => {
        if (e.target.name === 'currencyFrom') {
            setCurrFrom(e.target.value)
        }
        if (e.target.name === 'currencyTo') {
            setCurrTo(e.target.value)
        }
    }

    const handleInputCurrency = e => {
        setAmount(e.target.value);
    }

    // const currencyHistory = () => {
    //     const getHistory = JSON.parse(localStorage.getItem('history'));
    //     setCurrHistory(getHistory)
    // }


    // const currencyHistory = () => {
    //     // const amountResult = result;
    //     console.log(result);
    //     setCurrHistory([...currHistory, {amount, currFrom, result, currTo, date}])
    // }

    useEffect(() => {
        setCurrHistory([...currHistory, {amount, currFrom, result, currTo, date}])
    },[result])


    useEffect(() => {
        const getHistory = JSON.parse(localStorage.getItem('currencyHistory'))
       if (getHistory) setCurrHistory(getHistory)
    }, [])


    useEffect(() => {
        localStorage.setItem('currencyHistory', JSON.stringify(currHistory))
    }, [currHistory])


    return (
        <div className="converter">
            <h1>Currency Converter</h1>

            <div className="converter-element">
                <Link to={{ pathname: '/conversions', state: { currHistory } }}>
                    <button className="btn-option">History</button></Link>
                    <button>History</button>
                    <Modal
                     isShowing={isShowing}
                     hide={toggle}
                    />
            </div>

            {error !== null ? (<ErrorWall />) :
                <div className="converter-container">
                    <div className="converter-container-select">
                        <div className="converter-label">Convert from</div>
                        <div>
                            <input min="1" type="number" onKeyDown={e => ["e", "E", "+", "-"].includes(e.key) && e.preventDefault()} placeholder="Amount"
                                value={amount}
                                onChange={e => handleInputCurrency(e)} />
                        </div>
                        <CurrencySelect
                            currencyName={'currencyFrom'}
                            elements={elements}
                            currencySelected={currFrom}
                            currencyOnChange={e => handleSelectCurrency(e)}
                        />
                    </div>

                    <div className="converter-container-select">
                        <div className="converter-label">Convert to</div>
                        <CurrencySelect
                            currencyName={'currencyTo'}
                           elements={elements}
                            currencySelected={currTo}
                            currencyOnChange={e => handleSelectCurrency(e)}
                        />
                        <div>
                            <input type="number" placeholder="Amount Result"
                                readOnly
                                value={result}
                                onChange={e => handleInputCurrency(e)}
                            />
                        </div>
                        <button onClick={convertCurrency}>Convert</button>

                    </div>
                </div>
            }
        </div>
    )
}


