import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { Form, Field } from 'react-final-form';
import CurrencySelect from '../components/CurrencySelect';
import ErrorWall from '../components/Errors';
import './HomePage.css';

export default function HomePageTest() {

    const [elements, setElements] = useState([]);
    const [currHistory, setCurrHistory] = useState([]);
    const [amount, setAmount] = useState("");
    // const [result, setResult] = useState("");
    const [error, setError] = useState(null);
    const [date] = useState(new Date().toLocaleString().split(',')[0]);
    const [currFrom, setCurrFrom] = useState("USD");
    const [currTo, setCurrTo] = useState("PLN");

    const currencyAPI = 'https://api.exchangeratesapi.io/latest';

    let history = useHistory();


    //get currency value

    const getCurrValue = () => {
        currencyHistory();
        fetch(currencyAPI)
            .then(res => res.json())
            .then(data => {
                const allCurrency = ([data.base, ...Object.keys(data.rates)])
                setElements(allCurrency);
            })
            .catch(err => setError(true))
    }

    useEffect(() => {
        getCurrValue();
    }, []);

    useEffect(() => {
        console.log(elements)
        console.log(error)
    }, [elements, error]);


    // useEffect(() => {
    //     // currencyHistory();
    //     fetch(currencyAPI)
    //         .then(res => res.json())
    //         .then(data => {

    //             const allCurrency = ([data.base, ...Object.keys(data.rates)])
    //             setElements(allCurrency);


    //             // console.log(allCurrency);
    //             console.log(elements);
    //         }
    //         // ,
    //         //     (error) => {
    //         //         setError(error)
    //         //     }

    //         )
    // }, [elements])







    //set currency value
    const convertCurrency = () => {
        fetch(currencyAPI + '?base=' + currFrom + '&symbols=' + currTo)
            .then(res => res.json())
            .then(data => {
                const amountScore = amount * (data.rates[currTo])
                const storageHistory = JSON.parse(localStorage.getItem('history')) || [];
                const amountResult = amountScore.toFixed(2);

                setCurrHistory(storageHistory);
                currHistory.push({
                    amount,
                    currFrom,
                    amountResult,
                    currTo,
                    date
                });

                setCurrHistory(localStorage.setItem('history', JSON.stringify(currHistory)))

                // history function

                // history link

                currencyHistory();

                history.push({
                    pathname: '/konwersje',
                    state: {
                        currHistory,
                    }
                })
            })
    }

    //history
    const currencyHistory = () => {
        const getHistory = JSON.parse(localStorage.getItem('history'));
        setCurrHistory(getHistory)
    }


    //handle functions
    const handleSelectCurrency = e => {
        if (e.target.name === 'currencyFrom') {
            setCurrFrom(e.target.value)
        }
        if (e.target.name === 'currencyTo') {
            setCurrTo(e.target.value)
        }
    }


    const handleInputCurrency = e => { setAmount(e.target.value); console.log(amount) }





    //validation
    const requiredInput = value =>
        (value ? undefined : <span className="error-form">Wprowadź kwotę</span>);

    const numberInput = value =>
        value && isNaN(Number(value)) ?
            <span className="error-form">Wartość musi być liczbą</span> : undefined;

    const allValidators = (...validators) => value =>
        validators.reduce((error, validator) => error || validator(value), undefined);


    return (
        <div className="home-container">
            <div className="home-element">
                <Link to={{ pathname: '/konwersje', state: { currHistory } }}>
                    <button className="btn-option">Historia Konwersji</button></Link>
            </div>

            {error !== null ? (<ErrorWall />) :

                <div className="home-element">
                    <Form
                        onSubmit={convertCurrency}
                        // initialValues={setAmount(amount)}
                        initialValues={{amount}}
                    >
                        {({ handleSubmit }) => (
                            <form onSubmit={handleSubmit}>
                                <Field
                                    name="currencyAmount"
                                validate={allValidators(requiredInput, numberInput)}
                                // validate={numberInput}
                                >
                                    {({ input, meta }) => (
                                        <div>
                                            <div className="home-label"><label>Kwota</label></div>
                                            <input {...input} type="text" placeholder="Wprowadź kwotę" value={amount}
                                                onChange={e => handleInputCurrency(e)} />
                                            {meta.error && meta.touched && <div className="form-error">{meta.error}</div>}
                                        </div>
                                    )}
                                </Field>

                                <label name="selectFrom">Konwertuj z</label>
                                <Field name="selectFrom" >
                                    {({ select }) => (
                                        <CurrencySelect
                                            currencyName={'currencyFrom'}
                                            currencyElements={elements}
                                            currencySelected={currFrom}
                                            currencyOnChange={e => handleSelectCurrency(e)}
                                        />
                                    )}
                                </Field>
                                <label name="selectTo">Konwertuj na</label>

                                <Field name="selectTo" >
                                    {({ select }) => (
                                        <CurrencySelect
                                            currencyName={'currencyTo'}
                                            currencyElements={elements}
                                            currencySelected={currTo}
                                            currencyOnChange={e => handleSelectCurrency(e)}
                                        />
                                    )}
                                </Field>

                                <button className="btn-submit" type="submit" onClick={handleSubmit}>Konwertuj</button>

                            </form>
                        )}
                    </Form>
                </div>
            }
        </div>
    )
}
