import React from 'react';
import { Link } from 'react-router-dom';
import './Conversions.css';

export default function ConversionsTest(props) {

    const { currHistory } = props.location.state;

    function clearStorage() {
        localStorage.clear();
    }

    return (
        <div className="conversion-container">
            <div className="conversion-element">
                <Link to='/'><button className="btn-conversions" >Strona Główna</button></Link>
                {currHistory  !== null && (
                    <Link to='/'><button className="btn-conversions" onClick={clearStorage}>Wyczyść historię</button></Link>
                )}
            </div>

            <table className="conversion-table">
                {currHistory !== null && (
                    <thead>
                        <tr>
                            <th>Kwota bazowa</th>
                            <th>Wynik konwersji</th>
                            <th>Data</th>
                        </tr>
                    </thead>
                )}

                {currHistory  !== null ? currHistory.map(item =>
                    <tbody>
                        <tr>
                            <td><span>{item.amount}</span><span>{item.currencyFrom}</span></td>
                            <td><span>{item.amountResult}</span><span>{item.currencyTo}</span></td>
                            <td>{item.date}</td>
                        </tr>
                    </tbody>
                ) : <span className="conversion-info">Historia Konwersji jest pusta</span>}
            </table>
        </div>
    );
}
